// Defines the GameItem interface and exports the comprehensive arcadeGames array.

export interface GameItem {
  id: number | string;
  title: string;
  src: string;
  width: number;
  height: number;
  thumbnail: string;
  tags: string[];
  rating?: number;
  author: string;
  featured?: boolean;
  aiHint?: string;
}

export const genericThumbnail = "data:image/svg+xml;base64,";

function generateAiHint(title: string): string {
  const words = title.toLowerCase().split(' ').filter(word => word.length > 1 && !['a', 'the', 'of', 'and', 'for', 'in', 'on', 'to', 'with', '&', 'at', '2', '3', '4', '5', '6'].includes(word));
  if (words.length === 1 && words[0]) {
      return `${words[0]} game`;
  }
  if (words.length === 0) {
    return 'interactive game';
  }
  return words.slice(0, 2).join(' ');
}

function generateTags(title: string, defaultTags: string[] = ['html5']): string[] {
    const titleWords = title.toLowerCase().split(' ').filter(tag => tag.length > 2 && !['the', 'of', 'and', 'a', 'game', 'player', 'radio', 'simulator', 'classic', 'client', '2', '3d'].includes(tag) );
    const uniqueTags = [...new Set([...titleWords.slice(0,2), ...defaultTags])];
    return uniqueTags.slice(0, 4); // Limit to 4 tags
}

